import Card from '@material-tailwind/react/Card';
import CardBody from '@material-tailwind/react/CardBody';
import { useEffect, useState } from 'react';
import { getCookie } from 'assets/utils/helper';
import { removeCookie } from 'assets/utils/helper';
import { Button, Icon } from '@material-tailwind/react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/20/solid';
import { notify } from 'assets/utils/helper';
import copy from 'copy-to-clipboard';
import LoadingScreen from './LoadingScreen';
import { NavLink } from 'react-router-dom';

export default function TableApikeys() {
    const [loading, setLoading] = useState(true)
    const [loadingCreate, setLoadingCreate] = useState(false)
    const [loadingDelete, setLoadingDelete] = useState(false)
    const [data, setData] = useState([])
    let [isOpen, setIsOpen] = useState(false)
    let [apiName, setApiName] = useState('')
    let [error, setError] = useState(null)
    // const [paginate, setPaginate] = useState({
    //     current_page: 1,
    //     first_page_url: '',
    //     from: 1,
    //     last_page: 1,
    //     last_page_url: '',
    //     next_page_url: '',
    //     per_page: 10,
    //     prev_page_url: '',
    //     to: 1,
    //     total: 0,
    // });

    useEffect(() => {
        let token = getCookie("token")
        if (token === "" || token === undefined) {
            window.location.href = "/login"
        }
        setTimeout(() => {
            try {
                fetch(`${process.env.REACT_APP_BACKEND_URL}/api/v1/keys/list`, {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${getCookie('token')}`
                    }
                })
                .then(res => res.json())
                .then(data => {
                    if (data.success === true) {
                        setData(data)
                        setLoading(false)
                    } else if (data.error.error_code === 0) {
                        removeCookie("token");
                        window.location.href = "/login";
                    } else {
                        throw new Error(data.message)
                    }
                })
                .catch(err => {
                    throw new Error(err)
                })
            } catch (err) {
                throw new Error(err)
            }
        }, 500)
    }, [])

    const handlePagination = (page) => {
        setLoading(true)
        setTimeout(() => {
            try {
                fetch(`${process.env.REACT_APP_BACKEND_URL}/api/v1/keys/list?page=${page}`, {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${getCookie("token")}`
                    }
                })
                .then((res) => res.json())
                .then((data) => {
                    if (data.success === true) {
                        setData(data);
                        setLoading(false)
                    } else if (data.error.error_code === 0) {
                        // remove token
                        removeCookie("token")
                        window.location.href = "/login"
                    }
                })
                .catch((err) => {
                    console.log(err);
                })
            } catch (error) {
                throw error
            }
        }, 500)
    }

    const openModal = () => {
        setIsOpen(true)
        setApiName('')
        document.getElementById("name-api").value = ""
    }

    const handleCreateAPI = () => {
        setLoadingCreate(true)
        setTimeout(() => {
            try {
                fetch(`${process.env.REACT_APP_BACKEND_URL}/api/v1/keys/create`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${getCookie("token")}`
                    },
                    body: JSON.stringify({
                        name: apiName
                    })
                })
                .then((res) => res.json())
                .then((data) => {
                    if (data.success === true) {
                        handlePagination(1)
                        setLoadingCreate(false)
                        setIsOpen(false)
                    } else if (data.error.error_code === 0) {
                        // remove token
                        removeCookie("token")
                        window.location.href = "/login"
                    } else if (data.error.error_code === 'PROCESS_ERROR') {
                        setLoadingCreate(false)
                        setError(data.message)
                    }
                })
                .catch((err) => {
                    throw new Error(err)
                })
            } catch (error) {
                throw error
            }
        }, 500)
    }

    const handleDeleteAPI = (id) => {
        setLoadingDelete(true)
        setTimeout(() => {
            try {
                fetch(`${process.env.REACT_APP_BACKEND_URL}/api/v1/keys/delete/${id}`, {
                    method: "DELETE",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${getCookie("token")}`
                    }
                })
                .then((res) => res.json())
                .then((data) => {
                    if (data.success === true) {
                        notify('Deleted has been successfully');
                        handlePagination(1)
                        setLoadingDelete(false)
                    } else if (data.error.error_code === 0) {
                        // remove token
                        removeCookie("token")
                        window.location.href = "/login"
                    } else if (data.error.error_code === 'PROCESS_ERROR') {
                        setLoadingDelete(false)
                        notify(data.message);
                    }
                })
                .catch((err) => {
                    throw new Error(err)
                })
            } catch (error) {
